mapper.select:
SELECT u.id AS user_id, u.head_photo AS user_head_photo, u.nickname AS user_nickname, mc.id AS music_comment_id, mc.comment_content AS music_comment_content, mc.comment_time AS music_comment_time, mclc.num_of_like_comment AS num_of_like_comment_of_music FROM user u, music m, music_comment mc, (SELECT mc.id AS music_comment_id, mc.user_id AS music_comment_user_id, mc.music_id AS music_comment_music_id, count(CASE WHEN lc.user_id IS NOT NULL THEN 1 END) AS num_of_like_comment FROM music_comment mc LEFT JOIN like_comment lc ON mc.id = lc.comment_id GROUP BY mc.id) mclc WHERE m.id = mc.music_id AND mc.id = mclc.music_comment_id AND u.id = mc.user_id
AND m.id = #{music_id,jdbcType=BIGINT}
ORDER BY mc.comment_time DESC;
